```typescript
import { ai, googleAI } from './genkit';
import * as fs from 'fs/promises';
import * as path from 'path';

interface KnowledgeChunk {
  text: string;
  embedding: number[];
}

let knowledgeBase: KnowledgeChunk[] | null = null;

async function loadKnowledgeBase(): Promise<KnowledgeChunk[]> {
  if (knowledgeBase) return knowledgeBase;

  const ragPath = path.join(process.cwd(), 'rag-memory.json');
  const data = await fs.readFile(ragPath, 'utf-8');
  knowledgeBase = JSON.parse(data);
  return knowledgeBase!;
}

function cosineSimilarity(a: number[], b: number[]): number {
  const dotProduct = a.reduce((sum, val, i) => sum + val * b[i], 0);
  const magnitudeA = Math.sqrt(a.reduce((sum, val) => sum + val * val, 0));
  const magnitudeB = Math.sqrt(b.reduce((sum, val) => sum + val * val, 0));
  return dotProduct / (magnitudeA * magnitudeB);
}

export async function retrieveRelevantContext(query: string, topK: number = 3): Promise<string> {
  const chunks = await loadKnowledgeBase();

  const queryEmbeddingResponse = await ai.embed({
    embedder: googleAI.embedder('text-embedding-004'),
    content: query,
  });

  const queryEmbedding = queryEmbeddingResponse.embedding;

  const similarities = chunks.map(chunk => ({
    text: chunk.text,
    similarity: cosineSimilarity(queryEmbedding, chunk.embedding)
  }));

  similarities.sort((a, b) => b.similarity - a.similarity);

  return similarities
    .slice(0, topK)
    .map(item => item.text)
    .join('\n\n---\n\n');
}
```

